(62, 'my_first_project', '//动态图片障碍物类型\r\n// chomper: 食人花\r\n// flower: 太阳花\r\n// bird: 小鸟\r\n// crab: 螃蟹\r\n// fire: 火\r\n\r\n//静态图片障碍物类型\r\n// cacti: 仙人掌\r\n// spike1: 带钉子的方框1\r\n// spike2: 带钉子的方框2\r\n// spike3: 带钉子的方框3\r\n// tree: 树\r\n\r\n// ******************** Canvas 画布 ********************************************\r\ncanvas.width=600;\r\ncanvas.height=300;\r\n\r\nvar game = new LL.RunnerGame();\r\n\r\n// ******************** 障碍物函数 ********************************************\r\n\r\ngame.newScene()\r\n  //写文本的函数\r\n .text("XRD出品", 200, 100, "white")\r\n  //画动态图的函数\r\n .animation(flower, 200, 170)  \r\n //画静态图的函数\r\n .image(tree, 400, 170);  \r\n\r\n\r\ngame.newScene()\r\n  .image(spike2, 100, 150)\r\n  .image(cacti, 300, 170)\r\n  .animation(crab, 500, 180);\r\n\r\ngame.init();\r\n//game.testRun();\r\ngame.start();', '', 0, 62, 1, 1510336884, 1510337342),
(63, 'my_first_project', '// ******************** Variables 变量 ****************************************\r\ncanvas.width = 550;\r\ncanvas.height  = 550;\r\n\r\nvar background = "http://ou5n3ml3s.bkt.clouddn.com/mario_background_1.png";\r\nvar player = "http://ou5n3ml3s.bkt.clouddn.com/mario_mario.png";\r\nvar food = "http://ou5n3ml3s.bkt.clouddn.com/mario_mushroom.png";\r\n\r\nvar foodX = 10;\r\nvar foodY = 20;\r\nvar foodWidth = 40 ;\r\nvar foodHeight = 40 ;\r\nvar playerX = 30;\r\nvar playerY = 322;\r\nvar playerWidth = 128;\r\nvar playerHeight = 128;\r\n\r\nfunction draw() {\r\n\r\n  //draw image\r\n  image(background,0, 0,550,550); \r\n  image(food,foodX,foodY,64,64);\r\n  image(player,playerX,playerY,128,128);\r\n \r\n  //how to move the food\r\n  foodY += 5;\r\n  if(foodY>550){\r\n     foodY=0;\r\n    foodX=Math.random() * 500;\r\n  }\r\n\r\n  \r\n  //detect collision\r\n  if (detect("food","player")){\r\n    foodY = 550;\r\n    points+=18;\r\n  }\r\n  \r\n\r\n  //写分数函数\r\n    text("Points=" +points,180,80); \r\n  \r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n/////////////////////////////////\r\n// DO NOT CODE UNDER THIS LINE //\r\n/////////////////////////////////\r\nvar points = 0;\r\n \r\ncanvas = document.getElementById("canvas");\r\ncontext = canvas.getContext("2d");\r\ndocument.addEventListener("keydown", keydown_handler, false);\r\ndocument.addEventListener("keyup", keyup_handler, false); \r\ndocument.addEventListener(''touchstart'',touch, false);  \r\ndocument.addEventListener(''touchmove'',touch, false);  \r\ndocument.addEventListener(''touchend'',touch, false);\r\ndocument.addEventListener(''click'',touch, false);\r\n\r\n\r\nvar moveInterval = setInterval(function () {\r\n    draw();\r\n    handleButton();\r\n    handleJump();\r\n    handleLeft();\r\n    handleRight();\r\n}, 30);\r\n/////////////////////////////////\r\n//     键盘控制Player的代码     //\r\n/////////////////////////////////\r\nfunction keydown_handler(event) {\r\n    if (event.keyCode == 39) {\r\n        ismovingRight = true;\r\n    }\r\n    if (event.keyCode == 37) {\r\n        ismovingLeft = true;\r\n    }\r\n    if(isJumping === false) {\r\n        if (event.keyCode == 38) {\r\n            isJumping = true;\r\n            playerspeedY = -30;\r\n        }\r\n    }    \r\n}\r\n\r\nfunction keyup_handler(event) {\r\n    if (event.keyCode == 39) {\r\n        ismovingRight = false;\r\n    }\r\n    if (event.keyCode == 37) {\r\n        ismovingLeft = false;\r\n    }\r\n}\r\n\r\n\r\nfunction handleButton() {\r\n    isPhone = isMobile.any(); \r\n    if(isPhone) {      \r\n        image(''http://ou5n3ml3s.bkt.clouddn.com/mario_up.png'',50,460,80,80);  \r\n        image(''http://ou5n3ml3s.bkt.clouddn.com/mario_left.png'',320,460,80,80); \r\n        image(''http://ou5n3ml3s.bkt.clouddn.com/mario_right.png'',420,460,80,80);  \r\n    }\r\n}\r\n  \r\nvar touched = false;\r\nfunction touch(event) {\r\n    if(isPhone) {\r\n    touched = true;\r\n    if(isJumping === false) {\r\n        if(event.pageX > 50 && event.pageX < 130 && event.pageY > 460 && event.pageY < 540) {\r\n            isJumping = true;\r\n            playerspeedY = -30;\r\n        }\r\n    }\r\n    if(event.pageX > 320 && event.pageX < 400 && event.pageY > 460 && event.pageY < 540) {\r\n        pla', '<!DOCTYPE html>\n<html>\n<head>\n  <meta charset="utf-8">\n  <title>drop and catch</title>\n</head>\n<body>\n<canvas id="canvas"></canvas>\n<script src="http://orjajlo36.bkt.clouddn.com/summer4.js">\n  </scrip', 0, 63, 1, 1510380837, 1510579983),
(64, 'my_first_project', '// Let us code the future \r\nvar canvas = document.getElementById(''canvas'');\r\nvar ctx = canvas.getContext(''2d'');\r\ncanvas.width = 600;\r\ncanvas.height = 450;\r\n\r\nvar clock1 = "http://ou1htxdl4.bkt.clouddn.com/clk/biaopan-01.png";//方形表盘\r\nvar clock2 = "http://ou1htxdl4.bkt.clouddn.com/clk/biaopan-02.png";//圆形表盘\r\n\r\nvar sec = 0;\r\nvar min = 0;\r\nvar hour = 0;\r\nctx.lineCap = "round";\r\n\r\nrectangle(0,0,canvas.width,canvas.height,"lightgrey");\r\n\r\n/******* 主函数 *******/\r\n\r\nclock();\r\nsetInterval(clock,1000);\r\n\r\nfunction clock(){\r\n  \r\n  rectangle(100,300,100,100,"blue");\r\n  rectangle(400,300,100,100,"blue");\r\n  circle(300,230,170,"blue");\r\n  circle(170,130,70,"blue");\r\n  circle(430,130,70,"blue");\r\n  /********* 1 画出表盘 **********/\r\n  image(clock2,150,80,300,300);\r\n  image("http://owtvvc2bc.bkt.clouddn.com/1.png",267,148,80,160);\r\n\r\n  /********* 4 画出三根针 *********/\r\n  drawPointer(300,230,6,120,"green", sec * 6);\r\n  drawPointer(300,230,8,95,"red",min*6);\r\n  drawPointer(300,230,10,75,"black",hour*30);\r\n  \r\n  //让时针转动:初级版\r\n  sec += 1;\r\n  min += 1/60;\r\n  hour += 1/3600;\r\n  \r\n  //让时针转动：中级版\r\n  //当秒钟超过60时，变为0，分钟+1\r\n  if (sec >= 60) {\r\n    sec = 0;\r\n    min += 1;\r\n  } \r\n  //当分钟超过60时，变为0，小时+1\r\n  if (min >= 60) {\r\n    min = 0;\r\n    hour += 1;\r\n  }\r\n  //当小时超过12时，变为0\r\n  if (hour >= 12) {\r\n    hour = 0; \r\n  }\r\n  //2、闹钟功能\r\n}\r\n\r\n', '', 0, 64, 1, 1510389783, 1510389852),
(65, 'my_first_project', '// ******************** Variables 变量 ****************************************\r\ncanvas.width = 500;\r\ncanvas.height  = 500;\r\n\r\nvar background = "http://58pic.ooopic.com/58pic/14/11/45/64458PICSRK.jpg";\r\nvar player = "http://i65.tinypic.com/jqly7a.jpg";\r\nvar egg = "http://i68.tinypic.com/2it3iwj.png";\r\n\r\nvar eggX = 10;\r\nvar eggY = 20;\r\nvar playerX = 30;\r\nvar playerY = 420;\r\n\r\nfunction draw() {\r\n\r\n  //draw image\r\n  image(background,0, 0,500,500);\r\n  image(egg,eggX,eggY,40,40);\r\n  image(player,playerX,playerY,80,80);\r\n  \r\n  \r\n  //how to move the egg\r\n  eggY += 5;\r\n  if(eggY>500){\r\n     eggY=0;\r\n    eggX=Math.random() * 400;\r\n  }\r\n\r\n  \r\n  //detect collision\r\n  if (detect("egg","player")){\r\n    eggY = 510 ;\r\n    points+=1;\r\n  }\r\n  \r\n  \r\n  \r\n  \r\n  \r\n  \r\n  //写分数函数\r\n text("Points=" +points,180,80); \r\n  \r\n  \r\n}\r\n\r\n\r\nvar points = 0;\r\n\r\n\r\n\r\n\r\n\r\n\r\n/////////////////////////////////\r\n// DO NOT CODE UNDER THIS LINE //\r\n/////////////////////////////////\r\nvar eggWidth = 40 ;\r\nvar eggHeight = 40 ;\r\nvar playerWidth = 80;\r\nvar playerHeight = 80; \r\ncanvas = document.getElementById("canvas");\r\ncontext = canvas.getContext("2d");\r\nwindow.addEventListener("keydown", keypress_handler, false);\r\nvar moveInterval = setInterval(function () {\r\n    draw();\r\n}, 30);\r\n/////////////////////////////////\r\n//     键盘控制Player的代码       //\r\n/////////////////////////////////\r\nfunction keypress_handler(event) {\r\n    console.log(event.keyCode);\r\n    if (event.keyCode == 39) {\r\n        playerX+=10;\r\n    }\r\n    if (event.keyCode == 37) {\r\n        playerX-=10;\r\n    }\r\n}\r\n\r\n\r\nfunction image(source,x,y,w,h){\r\n  var img=new Image();\r\n  img.src=source;\r\n  ctx.drawImage(img,x,y,w,h);\r\n}\r\n\r\nfunction text(x,x1,y1) {\r\n    ctx.font = "30px Arial";\r\n    ctx.fillStyle = "red";\r\n    ctx.fillText(x, x1, y1);\r\n}\r\n\r\nfunction detect(object1,object2)\r\n{\r\n  if((object1=="player"&&object2=="egg")||(object1=="egg"&&object2=="player"))\r\n{\r\n   if ((eggX>playerX && eggX<playerX+playerWidth)&&(eggY+eggHeight>playerY&&eggY<playerY+playerHeight))\r\n    {\r\n    return true;\r\n   } \r\n}\r\n}\r\n\r\n\r\ncontext.font="24px ariel";\r\ncontext.fillStyle = "white";\r\n\r\n\r\n', '', 0, 65, 1, 1510471880, 1510471907),
