(145, '悠悠最爱吃蘑菇', 'var bg = "http://ou5n3ml3s.bkt.clouddn.com/planefight_bg.png";\r\n\r\nvar enemy = "http://ou5n3ml3s.bkt.clouddn.com/planefight_enemy.png";\r\nvar enemyX = 200;\r\nvar enemyY = 100;\r\n\r\nvar bullet = "http://ou5n3ml3s.bkt.clouddn.com/planefight_bullet.png";\r\nvar bulletX = 215;\r\nvar bulletY = 450;\r\n\r\nvar hero = "http://ou5n3ml3s.bkt.clouddn.com/planefight_hero.png";\r\nvar heroX = 200;\r\nvar heroY = 500;\r\n\r\nsetInterval(drawGame, 10);\r\n\r\nfunction drawGame() {\r\n  //1、画出背景\r\n  image(bg,0,0,500,600);\r\n\r\n  //2、画出敌机\r\n  image(enemy,enemyX,enemyY,60,60);\r\n  if (enemyY > 600)\r\n  {\r\n    enemyY = -30;\r\n    enemyX = 500 * Math.random();\r\n  }\r\n  enemyY += 2;\r\n  \r\n\r\n  //3、画出英雄\r\n  image(hero,heroX,heroY,50,50);\r\n\r\n  //4、画出英雄子弹\r\n  image(bullet,bulletX,bulletY,30,30);\r\n  if (bulletY < 0)\r\n  {\r\n    bulletY = heroY;\r\n    bulletX = heroX + 15;\r\n  }\r\n  bulletY -= 8;\r\n  \r\n  //5、子弹击中敌机\r\n  if (detect(bulletX,bulletY,30,30,enemyX,enemyY,60,60))\r\n  {\r\n    //重置敌机\r\n    //重置子弹\r\n    //分数加100\r\n    enemyY = 1000;\r\n    bulletY = -100;  \r\n    score += 100; \r\n  }\r\n  drawScore();\r\n  \r\n}', '<canvas id=''canvas'' height=''950'' width=''800''></canvas>\n<script src=''lib/utils.js''></script>\n', 0, 136, 1, 1511417856, 1514533191),
