550, stageHeight/2 - 100, 250, 100);\r\n    context.closePath();\r\n    context.fill();   \r\n    \r\n    context.font = "20px microsoft yahei";\r\n    context.fillStyle = ''#FFFFFF'';    \r\n    context.fillText(score, 570, 370);\r\n    \r\n  }\r\n\r\n  createjs.Ticker.setFPS(20);\r\n  createjs.Ticker.addEventListener("tick", tick);\r\n  function tick(event){  \r\n    stage.update();  \r\n \r\n    if (charactor.x == 329 && charactor.y == 270 && isMoving === 0){\r\n      score = "回家了，好开心！";\r\n      victory = 1; \r\n    }    \r\n    else if (charactor.x == 407 && charactor.y == 387 && isMoving === 0){\r\n      score = "这条裙子真漂亮！";   \r\n      victory = 1;     \r\n    }   \r\n    else if (charactor.x == 212 && charactor.y == 387 && isMoving === 0){ \r\n      score = "冰淇淋跟夏天最配了！";\r\n      victory = 1;       \r\n    }\r\n    else if (charactor.x > 95 && charactor.y > 114 && isMoving === 0) {\r\n      score = "哎呀，走错了！";\r\n      victory = 2;\r\n    }\r\n    Score();\r\n  }\r\n}\r\n\r\nvar soundInterval = setInterval(setSound, 1000);\r\nfunction setSound(){\r\n  if(victory == 1) {\r\nvar victorySound = new Audio(''http://ou5n3ml3s.bkt.clouddn.com/%E8%83%9C%E5%88%A9%E9%B8%9F%E5%8F%AB%E5%A3%B0.mp3'');\r\n  victorySound.play();\r\n  audi', '<!DOCTYPE html>\n<html>\n<head>\n  <meta charset="utf-8">\n  <title>Hackathon</title>\n</head>\n<body>\n<canvas id="canvas"></canvas>\n<script src="http://cdn.gbtags.com/EaselJS/0.7.1/easeljs.min.js"></script>\n<script src="http://cdn.gbtags.com/tweenjs/0.5.1/tweenjs.min.js"></script>  \n</body>\n</html>', 0, 156, 1, 1512269735, 1513402969),
