(745, 'my_first_project', '//==================画布===================\r\n\r\ncanvas.width = 600;\r\ncanvas.height = 600;\r\n//==================变量===================\r\nvar background = "";\r\n\r\nvar enemy = "http://orjajlo36.bkt.clouddn.com/splane111.png";\r\nvar enemyX = 200;\r\nvar enemyY = 100;\r\n\r\nvar bullet = "http://ou5n3ml3s.bkt.clouddn.com/planefight_bullet.png";\r\nvar bulletX = 215;\r\nvar bulletY = 450;\r\n\r\nvar hero = "http://ou5n3ml3s.bkt.clouddn.com/planefight_hero.png";\r\nvar heroX = 200;\r\nvar heroY = 500;\r\nvar score =10;\r\n\r\nsetInterval(drawGame, 10);\r\n\r\n\r\n//==================函数==================\r\n\r\n\r\nfunction drawGame() {\r\n  //1.绘制背景\r\n  drawBackground();\r\n  //2.控制英雄战机\r\n  drawHero();\r\n  //3.控制英雄子弹\r\n  drawBullet();\r\n  //4.控制敌机\r\n  drawEnemy();\r\n  //5.控制敌机子弹\r\n  drawEnemyBullet();\r\n  //6.检测碰撞\r\n  dectectCollusion();\r\n  drawScore();\r\n  \r\n}\r\n\r\n//1.绘制背景\r\nfunction drawBackground(){\r\n    image(background,0,0,500,600);\r\n    \r\n}\r\n\r\n//2.控制英雄战机\r\nfunction drawHero(){\r\n    //image(图片，横坐标，纵坐标，图片宽度，图片高度)\r\n    image(hero,heroX,heroY,60,60);\r\n    //heroY-=1\r\n    \r\n    \r\n    \r\n}\r\n\r\n//3.控制英雄子弹\r\nfunction drawBullet(){\r\n     image(bullet,bulletX,bulletY,30,50);\r\n     \r\n     bulletY-=4\r\n    if (bulletY<0)     \r\n   { bulletX=heroX       \r\n    bulletY=heroY \r\n       \r\n   }\r\n    \r\n    \r\n    \r\n\r\n\r\n//4.控制敌机\r\nfunction drawEnemy(){\r\n     image(enemy,enemyX,enemyY,60,40);\r\n     enemyY+=3\r\n     if (enemyY>600) {\r\n         enemyX = Math.random()*376;\r\n         enemyY = 0;\r\n         \r\n     }\r\n    \r\n}\r\n\r\n//5.控制敌机子弹\r\nfunction drawEnemyBullet(){\r\n    \r\n    \r\n}\r\n\r\n//6.检查碰撞\r\nfunction dectectCollusion(){\r\n    if (detect(bulletX,bulletY,20,40,enemyX,enemyY,60,40)) {\r\n        enemyY=1000;\r\n        bulletY=-100;\r\n        score+=10;\r\n    }\r\n}\r\n', '<canvas id=''canvas'' height=''950'' width=''800''></canvas>\n<script src=''li