(842, '王者肥鸡', 'console.log(456);\r\n\r\ncanvas.width = 375;\r\ncanvas.height = 600;\r\nvar background ="http://tse1.mm.bing.net/th?id=OIP.iC_2XYx2BWXwZnZpDkxrYQHaHD&pid=15.1"\r\nvar enemy = "http://tse3.mm.bing.net/th?id=OIP.gtc1hmG9IOPa9dJbXXfs6gHaGG&pid=15.1";\r\nvar enemyX = 200;\r\nvar enemyY = 100;\r\nvar bullet = "https://i1.muzisoft.com/uploads/arc/201711/258971_2_1511493055T9uv.jpg";\r\nvar bulletX = 215;\r\nvar bulletY = 450;\r\nvar hero = "http://tse4.mm.bing.net/th?id=OIP.rNWnD8SanjWOowhSkNJS-gHaFf&pid=15.1";\r\nvar heroX = 200;\r\nvar heroY = 500;\r\nvar score =0;\r\nvar bgm = "http://ou1htxdl4.bkt.clouddn.com/HongKong/music/bgMusic.mp3";\r\nvar ebulletY=100;\r\nvar ebulletX=210;\r\n\r\n\r\nplayAudio(bgm,false);\r\n\r\nsetInterval(drawGame, 10);\r\nfunction drawGame() {\r\n  //1.绘制背景\r\n  drawBackground();\r\n  //2.控制英雄战机\r\n  drawHero();\r\n  //3.控制英雄子弹\r\n  drawBullet();\r\n  //4.控制敌机\r\n  drawEnemy();\r\n  //5.检测碰撞\r\n  detectCollusion();\r\n  enemyBullet()\r\n  drawScore();\r\n  drawXY(clickX,clickY);\r\n}\r\n//1.绘制背景function drawBackground(){\r\n    image(background,0,0,500,600);\r\n    \r\n}\r\n\r\n//2.控制英雄战机\r\nfunction drawHero(){\r\n    //image(图片，横坐标，纵坐标，图片宽度，图片高度)\r\n    image(hero,heroX,heroY,60,60);\r\n    \r\n}\r\n//3.控制英雄子弹\r\nfunction drawBullet(){\r\n    image(bullet,bulletX,bulletY,20,40);\r\n    bulletY-=10;\r\n    if (bulletY < 0) {\r\n        bulletX = heroX+20;\r\n        bulletY = heroY;\r\n    }\r\n    \r\n}\r\n//4.控制敌机\r\nfunction drawEnemy(){\r\n    image(enemy,enemyX,enemyY,60,60);\r\n    enemyY+=2;\r\n    if (enemyY>600) {\r\n        enemyY=0;\r\n        enemyX = Math.random()*1;\r\n        \r\n    }\r\n    \r\n}\r\n//5.检查碰撞\r\nfunction detectCollusion(){\r\n    \r\n    if (detect(bulletX,bulletY,20,40,enemyX,enemyY,60,60)) {\r\n        enemyY=1000;\r\n        bulletY=-100;\r\n        score+=1000 ;\r\n    }\r\n}\r\n\r\nfunction enemyBullet(){\r\n    \r\n  \r\nimage("http://pic.paopaoche.net/up/2015-12/2015123017232664283.png",ebulletX,ebulletY,40,65);\r\n\r\n  ebulletY+=3;\r\n  \r\n  if(ebulletY>600)\r\n  {\r\n      ebulletY=enemyY;\r\n      ebulletX=enemyX+10;\r\n  }\r\n    \r\n    \r\n    \r\n}\r\n\r\n\r\nfunction playMusic(src, loop) {\r\n    var bgm = new Audio(src);\r\n    if (loop) {\r\n        bgm.loop = true;\r\n    } else {\r\n        bgm.loop = false;\r\n    }\r\n    bgm.play();\r\n    // bgm.play();\r\n}', '', 0, 293, 1, 1514870025, 1514873948),
