(881, '3', '//==================画布===================\r\ncanvas.width = 375;\r\ncanvas.height = 598;\r\n\r\n//==================变量===================\r\n\r\nvar background = "https://ss0.bdstatic.com/70cFuHSh_Q1YnxGkpoWK1HF6hhy/it/u=3924134038,2630851534&fm=200&gp=0.jpg";\r\n\r\nvar enemy = "http://otde8iv1i.bkt.clouddn.com/i_plane4.png?imageView2/5/w/80/h/80";\r\nvar enemyX = 200;\r\nvar enemyY = 100;\r\n\r\nvar bullet = "http://otde8iv1i.bkt.clouddn.com/i_bullet5.png?imageView2/5/w/80/h/80";\r\nvar bulletX = 215;\r\nvar bulletY = 450;\r\n\r\nvar hero = "http://otde8iv1i.bkt.clouddn.com/i_plane5.png?imageView2/5/w/80/h/80";\r\nvar heroX = 300\r\nvar heroY = 300;\r\nvar score =100;\r\n\r\nsetInterval(drawGame, 10);\r\n\r\nvar enemy1 = "http://divine-gate.net/img/unit/tex_chara_l_240.png";\r\nvar enemy1X = 200;\r\nvar enemy1Y = 100;\r\n\r\n\r\n\r\nvar ebulletX =187\r\nvar ebulletY =108\r\n//==================函数==================\r\n\r\n\r\nfunction drawGame() {\r\n  //1.绘制背景\r\n  drawBackground();\r\n  //2.控制英雄战机\r\n  drawHero();\r\n  //3.控制英雄子弹\r\n  drawBullet();\r\n  //4.控制敌机\r\n  drawEnemy();\r\n  //5.控制敌机子弹\r\n  drawEnemyBullet();\r\n  //6.检测碰撞\r\n  dectectCollusion();\r\n  drawScore();\r\n  \r\n}\r\n\r\n//1.绘制背景\r\nfunction drawBackground(){\r\n    image(background,0,0,500,600);\r\n    \r\n}\r\n\r\n//2.控制英雄战机\r\nfunction drawHero(){\r\n    //image(图片，横坐标，纵坐标，图片宽度，图片高度)\r\n    image(hero,heroX,heroY,0,200);\r\n    \r\n    \r\n    \r\n    \r\n}\r\n\r\n//3.控制英雄子弹\r\nfunction drawBullet(){\r\n    image(bullet,bulletX,bulletY,80,80);\r\n    bulletY-=10\r\n   if (bulletY<0) {\r\n      bulletY=heroY+230\r\n      bulletX=heroX\r\n   } \r\n    \r\n    \r\n}\r\n//4.控制敌机\r\nfunction drawEnemy(){\r\n    image(enemy,enemyX,enemyY,00,300);\r\n    enemyY+=1\r\n    if (enemyY>600){\r\n        enemyY = 0\r\n        enemyX=Math.random()*300\r\n    }\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n//5.控制敌机子弹\r\nfunction drawEnemyBullet(){\r\n    image(''http://otde8iv1i.bkt.clouddn.com/i_bullet4.png?imageView2/5/w/80/h/80'',ebulletX,ebulletY,50,50)\r\n \r\n ebulletY+=5\r\n if(ebulletY>600){\r\n     ebulletX=enemyX\r\n     ebulletY=enemyY\r\n }\r\n \r\n   \r\n}\r\n\r\n //6.检查碰撞\r\nfunction dectectCollusion(){\r\n     if (detect(bulletX,bulletY,20,40,enemyX,enemyY,60,60)) {\r\n         enemyY=1000;\r\n         bulletY=-100;\r\n         score+=10;\r\n     }\r\n    \r\n}\r\n    if ( detect(ebulletX,ebulletY,100,100,heroX,heroY,125,125) ) {\r\n         heroX=100\r\n         heroY=100\r\n         score-=15 }\r\n\r\n\r\n', '<canvas id=''canvas'' height=''950'' width=''800''></canvas>\n<script src=''lib/utils.js''></script>\n', 0, 292, 1, 1514871818, 1514875474),
