(977, '星球大战绝密', '//==================画布===================\r\n\r\ncanvas.width = 500;\r\ncanvas.height = 600;\r\n\r\n//==================变量===================\r\n\r\nvar background = "http://img2.imgtn.bdimg.com/it/u=2836887445,2900964953&fm=200&gp=0.jpg";\r\n\r\nvar enemy = "http://otde8iv1i.bkt.clouddn.com/i_plane4.png?imageView2/5/w/80/h/80";\r\nvar enemyX = 200;\r\nvar enemyY = 100;\r\n\r\nvar bullet = "http://otde8iv1i.bkt.clouddn.com/i_bullet4.png";\r\nvar bulletX = 215;\r\nvar bulletY = 450;\r\n\r\nvar hero = "http://otde8iv1i.bkt.clouddn.com/i_plane5.png?imageView2/5/w/80/h/80";\r\nvar heroX = 200;\r\nvar heroY = 500;\r\n\r\nvar score =10000000000000000;\r\n\r\nvar ebulletX=210;\r\nvar ebulletY=150;\r\n\r\nvar Dx=50;\r\nvar Dy=50;\r\n\r\n\r\nsetInterval(drawGame, 10);\r\n\r\n\r\n//==================函数==================\r\n\r\n\r\nfunction drawGame() {\r\n\r\n\r\n  drawBackground();\r\n \r\n  drawHero();\r\n \r\n  drawBullet();\r\n \r\n  drawEnemy();\r\n \r\n  drawEnemyBullet();\r\n\r\n  dectectCollusion();\r\n  \r\n  drawScore();\r\n  \r\n  drawDiamond();\r\n}\r\n\r\n//绘制背景\r\nfunction drawBackground(){\r\n    image(background,0,0,500,600);\r\n    \r\n}\r\n\r\n//控制英雄战机\r\nfunction drawHero(){\r\n   \r\n    image(hero,heroX,heroY,60,100);\r\n}\r\n\r\n//控制英雄子弹\r\nfunction drawBullet(){\r\n     image(bullet,bulletX,bulletY,10,10);\r\n     \r\n     bulletY-=10\r\n    if (bulletY<0){\r\n    bulletX=heroX+15\r\n    bulletY=heroY\r\n    }\r\n}\r\n\r\n//控制敌机\r\nfunction drawEnemy(){\r\n     image(enemy,enemyX,enemyY,300,300);\r\n     enemyY+=1\r\n     if (enemyY>600) {\r\n         enemyX = Math.random()*376;\r\n         enemyY = 0;\r\n         \r\n     }\r\n    \r\n}\r\n\r\n//控制敌机子弹\r\nfunction drawEnemyBullet(){\r\n   \r\nimage(''http://otde8iv1i.bkt.clouddn.com/i_bullet5.png?imageView2/5/w/80/h/80'',ebulletX,ebulletY,100,110); \r\n\r\nebulletY+= 2;\r\nif(ebulletY>canvas.height){\r\n    ebulletX=enemyX+10;\r\n    ebulletY=enemyY;\r\n}\r\n\r\n}\r\n\r\n//绘制钻石\r\nfunction drawDiamond(){\r\n   \r\nimage(''http://otde8iv1i.bkt.clouddn.com/i_bullet4.png'',Dx,Dy,30,30);; \r\n\r\nDy+=1;\r\nif(Dy>canvas.height){\r\n    Dy=0;\r\n    Dx=Math.random()*400;\r\n}\r\n}\r\n\r\n\r\n\r\n//6.检查碰撞\r\nfunction dectectCollusion(){\r\n    if (detect(bulletX,bulletY,20,40,enemyX,enemyY,60,60)) {\r\n        enemyY=1000;\r\n       bulletY=-100;\r\n        score+=10;\r\n    }\r\n    \r\n   if (detect(ebulletX,ebulletY,20,40,heroX,heroY,60,60)) {\r\n        heroX=1000;\r\n       heroY=-100;\r\n        score=0;\r\n    }\r\n    if (detect(Dx,Dy,20,40,heroX,heroY,60,60)) {\r\n        Dx=1000;\r\n       Dy=-100;\r\n        score+=40;\r\n    }\r\n    \r\n}\r\n\r\n//绘制钻石\r\nfunction drawDiamond(){\r\n   \r\nimage(''http://otde8iv1i.bkt.clouddn.com/i_bullet4.png?imageView2/5/w/80/h/80'',Dx,Dy,100,100);; \r\n\r\nDy+=1;\r\nif(Dy>canvas.height){\r\n    Dy=0;\r\n    Dx=Math.random()*400;\r\n}\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '<canvas id="canvas"></canvas>\n<script src="lib/utils.js"></script>', 0, 499, 1, 1514880950, 1515486348),
