 drawDiamond(){\r\n   \r\nimage(''http://otde8iv1i.bkt.clouddn.com/i_bullet4.png'',Dx,Dy,54,53);; \r\n\r\nDy+=6;\r\nif(Dy>canvas.height){\r\n    Dy=0;\r\n    Dx=Math.random()*400;\r\n}\r\n}\r\n\r\n\r\n\r\n//6.检查碰撞\r\nfunction dectectCollusion(){\r\n    if (detect(bulletX,bulletY,20,40,enemyX,enemyY,60,60)) {\r\n        enemyY=1000;\r\n       bulletY=-100;\r\n        score+=10;\r\n    }\r\n    \r\n   if (detect(ebulletX,ebulletY,20,40,heroX,heroY,60,60)) {\r\n        heroX=1000;\r\n       heroY=-100;\r\n        score=0;\r\n    }\r\n    if (detect(Dx,Dy,20,40,heroX,heroY,60,60)) {\r\n        Dx=1000;\r\n       Dy=-100;\r\n        score+=40;\r\n    }\r\n    \r\n}\r\n', '', 0, 272, 1, 1515998610, 1515998610),
